{% if product.metafields.custom.video %}
<style>
  /* 视频容器保持16:9比例 */
  .video-wrapper {
    position: relative;
    width: 100%;
    padding-bottom: 56.25%; /* 16:9 */
    height: 0;
    overflow: hidden;
  }

  /* 视频内容填充容器 */
  .video-wrapper iframe,
  .video-wrapper video {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: block;
  }

  /* Accordion 样式调整 */
  .accordion__summary {
    display: flex;
    align-items: center;
    position: relative;
    margin: 0;
    padding: 1rem 8px; /* 添加左右边距8px */
    list-style: none;
    font-size: 14px;
    text-transform: uppercase;
    font-weight: bold;
    line-height: 1.4;
    cursor: pointer;
  }

  .accordion__summary img {
    width: 18px; /* 图标大小 */
    height: 18px;
    margin-right: 10px; /* 图标与文字的间距 */
  }

  .accordion__summary svg {
    width: 12px; /* 下拉箭头宽度 */
    height: 8px; /* 下拉箭头高度 */
    margin-left: auto; /* 下拉箭头靠右对齐 */
    stroke-width: 0; /* 增加线条宽度 */
    stroke-linecap: round; /* 圆角线条 */
  }

  /* 模块底部分隔线 */
  .accordion {
    border-bottom: 1px solid #e0e0e0; /* 添加分割线 */
  }

  /* 分割线样式 */
  .price-divider {
    width: 100%;
    border-top: 1px solid #000; /* 黑色分割线 */
    margin: 1rem 0; /* 添加上下间距 */
  }
</style>

<details 
  class="accordion" 
  style="display: block; margin: 0; padding: 0; list-style: none;"
>
  <summary 
    class="accordion__summary"
    role="button" 
    aria-expanded="false" 
    aria-controls="ProductAccordion"
  >
    <img 
      src="https://cdn.shopify.com/s/files/1/0243/8768/2340/files/play-ICON.png?v=1739512641" 
      alt="Play Icon"
    >
    <span style="display: inline-block;">
      Setup Video
    </span>
    <svg 
      aria-hidden="true" 
      focusable="false" 
      viewBox="0 0 12 8"
    >
      <path 
        fill="none" 
        d="M1 1l5 5 5-5" 
        stroke="currentColor" 
        stroke-width="2.5" /* 增加线条宽度 */
        stroke-linecap="round" /* 圆角线条 */
      />
    </svg>
  </summary>

  <div 
    class="accordion__content" 
    style="box-sizing: border-box; padding-bottom: 1rem; border-bottom: none;"
  >
    <!-- 视频内容 -->
    <div class="video-wrapper">
      {{ product.metafields.custom.video }}
    </div>
  </div>
</details>

{% if forloop.last == false %}
<!-- 添加分割线 -->
<div class="price-divider"></div>
{% endif %}
{% endif %}

